/*==================================================
src/components/AccountBalance.js

The AccountBalance component displays account balance. It is included in other page views.
==================================================*/
import React, {Component} from 'react';
import Credits from './Credits';
import Debits from './Debits';
class AccountBalance extends Component {
  // Display account balance

  render() {
    let bal = 0;
    for (let cred in this.props.creditList)
      bal += cred;
    for (let cred in this.props.debitList)
      bal -= cred;
    //bal = bal + this.props.accountBalance;
    return (
      <div>
        Balance: {this.props.accountBalance + bal}
      </div>
    );
  }
}

export default AccountBalance;
