/*==================================================
src/App.js

This is the top-level component of the app.
It contains the top-level state.
==================================================*/
import React, {Component} from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import axios from 'axios'; 

// Import other components
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import LogIn from './components/Login';
import Credits from './components/Credits';
import Debits from './components/Debits';

class App extends Component {
  constructor() {  // Create and initialize state
    super(); 
    this.state = {
      accountBalance: 0.00, 
      creditList: [],
      debitList: [],
      currentUser: {
        userName: '',
        memberSince: '',
      }
    };
  }
  addCredit = (creditNum) =>
  {
    let newBalance = this.state.accountBalance + creditNum;
    this.setState({accountBalance: newBalance});
  }
  addDebit = (debitNum) =>
  {
    let newBalance = this.state.accountBalance - debitNum;
    this.setState({accountBalance: newBalance});
  }

  async componentDidMount()
  {
    let balance = 0.00;
    let apiCredits = 'https://johnnylaicode.github.io/api/credits.json';  // Link to remote API endpoint to get credits
    let apiDebits = 'https://johnnylaicode.github.io/api/debits.json';
    try {  // Accept success response as array of JSON objects (users)
      let responseCredits = await axios.get(apiCredits);
      //console.log(responseCredits);  // Print out response
      // To get data object in the response, need to use "response.data"
      this.setState({creditList: responseCredits.data});  // Store received data in state's "creditList" object
      for(let cred in responseCredits.data)
        balance += cred.amount;
      let responseDebits = await axios.get(apiDebits);
      //console.log(responseDebits);  // Print out response
      // To get data object in the response, need to use "response.data"
      this.setState({debitList: responseDebits.data}); 
      for(let cred in responseDebits.data)
        balance -= cred.amount;
      balance += this.state.accountBalance;
      this.setState({accountBalance: balance});
    } 
    catch (error) {  // Print out errors at console when there is an error response
      if (error.response) {
        // The request was made, and the server responded with error message and status code.
        console.log(error.response.data);  // Print out error message (e.g., Not Found)
        console.log(error.response.status);  // Print out error status code (e.g., 404)
      }    
    }
  }

  // Update state's currentUser (userName) after "Log In" button is clicked
  mockLogIn = (logInInfo) => {  
    const newUser = {...this.state.currentUser};
    newUser.userName = logInInfo.userName;
    this.setState({currentUser: newUser})
  }

  // Create Routes and React elements to be rendered using React components
  render() {  
    // Create React elements and pass input props to components
    const HomeComponent = () => (<Home accountBalance={this.state.accountBalance}/>)
   // const HomeComponent = () => (<Home accountBalance={this.state.accountBalance} creditList={this.state.creditList} debitList={this.state.debitList} />)
    const UserProfileComponent = () => (
      <UserProfile userName={this.state.currentUser.userName} memberSince={this.state.currentUser.memberSince} />
    )
    const LogInComponent = () => (<LogIn user={this.state.currentUser} mockLogIn={this.mockLogIn} />)
    const CreditsComponent = () => (<Credits credits={this.state.creditList} />) 
    const DebitsComponent = () => (<Debits debits={this.state.debitList} />) 

    // Important: Include the "basename" in Router, which is needed for deploying the React app to GitHub Pages
    return (
      <Router basename="/bank-of-react-example-code-gh-pages">
        <div>
          <Route exact path="/" render={HomeComponent}/>
          <Route exact path="/userProfile" render={UserProfileComponent}/>
          <Route exact path="/login" render={LogInComponent}/>
          <Route exact path="/credits" render={CreditsComponent}/>
          <Route exact path="/debits" render={DebitsComponent}/>
        </div>
      </Router>
    );
  }
}

export default App;
